import { Modal, Button, Form, Input, Checkbox, Image } from 'antd'
const { TextArea } = Input
import style from '../index.less'
import { useState } from 'react'
import { useIntl } from 'umi'
type IModal = {
  visible: boolean
  uploadImg: string
  onHandleOk: () => void
  onHandleCancel: () => void
}

export default function Forget({
  visible,
  onHandleOk,
  uploadImg,
  onHandleCancel
}: IModal) {
  const [freeLoginCheck, setFreeLoginCheck] = useState(false)

  const { formatMessage } = useIntl()
  const [form] = Form.useForm()

  const formItemLayout = {
    labelCol: {
      xs: { span: 24 },
      sm: { span: 6 }
    },
    wrapperCol: {
      xs: { span: 24 },
      sm: { span: 14 }
    }
  }

  const handleFreeLogin = (): void => {
    setFreeLoginCheck(!freeLoginCheck)
  }

  const handleOk = () => {}

  const handleCancel = () => {
    onHandleCancel && onHandleCancel()
  }

  const onFinish = () => {
    form.setFieldsValue({})
    console.log('Success:', form.getFieldsValue())
  }

  const onFinishFailed = (errorInfo: any) => {
    console.log('Failed:', errorInfo)
  }

  return (
    <Modal
      title={'分享导图'}
      visible={visible}
      onOk={onFinish}
      width="50%"
      onCancel={handleCancel}
    >
      <Form
        name="basic"
        onFinish={onFinish}
        onFinishFailed={onFinishFailed}
        form={form}
        {...formItemLayout}
      >
        <Form.Item label={'预览'}>
          <Image
            width={190}
            height={100}
            src={uploadImg}
            fallback="data:image/png;base64,"
          />
        </Form.Item>

        <Form.Item
          name="title"
          label={'标题'}
          rules={[{ required: true, message: '名字忘了吗？' }]}
        >
          <Input placeholder={formatMessage({ id: 'input.请输入' })} />
        </Form.Item>
        <Form.Item
          name="content"
          label={'简介'}
          rules={[{ required: true, message: '交白卷可不行！！!' }]}
        >
          <TextArea
            rows={4}
            placeholder={formatMessage({ id: 'input.请输入' })}
            maxLength={100}
          />
        </Form.Item>
        <Form.Item name="status" label={'开放评论'}>
          <Checkbox
            checked={freeLoginCheck}
            onChange={handleFreeLogin}
          ></Checkbox>
        </Form.Item>
      </Form>
    </Modal>
  )
}
